def question_deux(big_data)
	handle_numero = 0
	big_data.each do |name|
		for i in 0..9
			if name.include? i.to_s
				handle_numero += 1
			end
		end
	end

	puts "nombre d'handle qui contiennent un numero: #{handle_numero}"
end

#-----------------------------------------------------------------#

def question_trois(big_data)

	a = 0
	u = 0
	d = 0
	e = 0
	aude = 0 #nombre d'aude

	big_data.each do |name|
		name.split('').each do |char|
			if char.downcase == "a" && a == 0 && u == 0 && d == 0 && e == 0
				a = 1
			end

			if char.downcase == "u" && u == 0 && a == 1 && d == 0 && e == 0
				u = 1
			end

			if char.downcase == "d" && d == 0 && a == 1 && u == 1 && e == 0
				d = 1
			end

			if char.downcase == "e" && e == 0 && a == 1 && u == 1 && d == 1
				e = 1
			end

			if a == 1 && u == 1 && d == 1 && e == 1
				aude += 1
				a = 0
				u = 0
				d = 0
				e = 0
			end
		end
	end
	puts "nombre d'handle contiennent les 4 lettres du prenom Aude a la suite: #{aude}"
end

