def question_quatre(big_data)
	nom_maj = 0
	big_data.each do |name|
		for i in 'A'..'Z'
			if name[1] == i
				nom_maj += 1
			end
		end
			
	end
	puts "nombre de nom qui commencent par une majuscule : #{nom_maj}"
end

#----------------------------------------------------------------------------#

def question_cinq(big_data)
	nbr_maj_nom = 0
	j = 0

	big_data.each do |name|
		long = name.length
		for i in 'A'..'Z'
			for j in 0..long	
			   if name[j] == i
			   		nbr_maj_nom += 1
			   		break
			   	end
			end
			break if name[j] == i
		end
	end

	puts "nombre de nom qui contiennent une majuscule : #{nbr_maj_nom}"

end

#------------------------------------------------------------------------------#

def question_six(big_data)
	nbr_de_tiree = 0

	big_data.each do |name|
		long = name.length
		for i in 0..long
			if name[i] == "_"
				nbr_de_tiree += 1
			end
		end
	end

	puts "nombre de _ dans tous les pseudos confondus: #{nbr_de_tiree}"
end

