def question_huit(big_data)
	les_50_nom_cours = []
	name_long = []

	big_data.each do |name|
		name_long << name.length
	end

	name_long.sort!
	nombre = 49

	big_data.each do |name|
	 	if name.length <=  name_long[49]
	 		les_50_nom_cours << name
	 		nombre -=1
	 	end
	 	break if nombre <= 0
	end

	puts "les 50 nom les plus cours" 
	puts les_50_nom_cours
end

